mapper.select:
select i.type, i.manager, d.quantity as quantityNow, d.id as drugId, d.`name` as drugName,d.price,d.specification,i.quantity,i.ammount,s.name as supplierName,i.gmt_created from app_inventory_record i,app_drug d,app_supplier s where i.drug_id = d.id and d.supplier_id = s.id and i.type in('1','2')
order by ${sort} ${order}
order by gmt_created desc
limit ${offset}, ${limit}

select i.comment, i.type, i.manager, d.quantity as quantityNow, d.id as drugId, d.`name` as drugName,d.price,d.specification,i.quantity,i.ammount,s.name as supplierName,i.gmt_created from app_inventory_record i,app_drug d,app_supplier s where i.drug_id = d.id and d.supplier_id = s.id and i.type in('3','4')
order by ${sort} ${order}
order by gmt_created desc
limit ${offset}, ${limit}
